package com.whut.umrhamster.graduationproject.utils.service;import com.whut.umrhamster.graduationproject.model.bean.Live;import com.whut.umrhamster.graduationproject.utils.http.HttpData;import java.util.List;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.PUT;import retrofit2.http.Query;public interface LiveService {    @GET("live")    Call<HttpData<List<Live>>> getAllLive(@Query("all") boolean all);    @GET("live")    Call<HttpData<List<Live>>> getLiveByType(@Query("type")int type);    @GET("live")    Call<HttpData<List<Live>>> getLiveByKeyword(@Query("keyword")String keyword);    @GET("live")    Call<HttpData<List<Live>>> getLiveLimit10(@Query("start")int start);    @PUT("live")    Call<HttpData> updateLiveViewers(@Query("liveId")int liveId, @Query("operation")int operation);}